import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";

const Profile= () => {
  const [profileEmail, setProfileEmail] = useState("");
  const [profileName, setProfileName] = useState("");
  /*const [profileCell, setProfileCell] = useState(""); */
  const [profileAvatar, setProfileAvatar] = useState("");


  const profileData = async () => {
    try {
      const res = await axios.get("https://reqres.in/api/users?page=2");
     /* setProfileCell(res.data.results[0].cell); */

      setProfileEmail(res.data.results.email);
      setProfileName(
        `${res.data.results.first_name} ${res.data.results.last_name}`
      );
      setProfileAvatar(res.data.results.avatar);

    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    profileData();
  }, []);

  return (
    <div>
      <button onClick={() => profileData()}>New Person</button>
      <div className="card">
        <img src={profileAvatar} alt={profileAvatar} style={{ width: "100%" }} />
        <h1>{profileName}</h1>
        <p className="title">{profileEmail}</p>

        <p>
          <button>Contact</button>
        </p>
      </div>
    </div>
  );
};

export default Profile;
